import React, { Component } from 'react'
import ReactDOM from 'react-dom';


// color scheme https://www.schemecolor.com/coral-blue.php
class App extends Component {
  constructor(props) {
    super(props);
  }

  render() {
    return (
      <div className="App">
        <FrameArea>
        </FrameArea>
      </div>
    )
  }
}

// area shows the video and is the region where the bounding-box(es)
// can be moved 
class FrameArea extends React.Component {

  constructor(props) {
    super(props);
    this.state = { 
      apiResponse: "", 
      frameNumber: 1, //start counting at 1
      // props for BoundingBox
      l: 465,
      t: 113,
      h: 34,
      w: 34,
    };
    this.handleBBoxUpdate = this.handleBBoxUpdate.bind(this)
  }

  // https://stackoverflow.com/questions/23450534/how-to-call-a-python-function-from-node-js
  // https://www.freecodecamp.org/news/create-a-react-frontend-a-node-express-backend-and-connect-them-together-c5798926047c/
  // ref: https://stackoverflow.com/questions/51845170/how-to-deploy-a-node-js-app-that-runs-a-python-script-in-azure
  //connect to node.js (code in api folder)
  runTracker() {
    console.log('run trackers')
    fetch("http://localhost:9000/callPython/left/"+ this.state.l +"/top/"+this.state.t +"/width/"+this.state.w +"/height/"+this.state.h +"/") 
      .then(res => res.text())
      .then(res => this.setState({ apiResponse: res}));
  }

  // run tracker (runopencvtrackers.py)
  // TODO make t l w h into state and give as props to bbox, send to call python 
  render(){
    return (
      <div id="FrameArea">
      <BoundingBox l = {this.state.l} t = {this.state.t} w = {this.state.w} h = {this.state.h} handleBBoxUpdate = {this.handleBBoxUpdate}></BoundingBox> 
      <img id = "frame" src= {this.getFrame()}  alt="" width="500" draggable="false" ></img>
      <button id="btn_runtracker" onClick = {() => this.runTracker()}>run tracker</button>
      <p>response: {this.state.apiResponse}</p>
      <div className="slidecontainer">
        <input type="range" min="1" max="100" value={this.state.frameNumber} className="slider" id="myRange" onChange={this.handleSliderChange} onInput = {this.handleSliderInput} />
      </div>
      </div>
    )
  }

  getFrame(){
    console.log("images/"+ this.pad(this.state.frameNumber,8) +".jpg")
    return "images/"+ this.pad(this.state.frameNumber,8) +".jpg"  //number with 8 digits pad with leading zeros
  }

  pad(num, size) {
    var s = num+"";
    while (s.length < size) s = "0" + s;
    return s;
}

  //TODO -> slider component
  handleSliderInput= (event) => {
    this.setState({

      slidervalue: event.value,
    })
  }
  //TODO -> slider component
  handleSliderChange  = (event) => {
    console.log(event.type)

  }

  componentDidMount() {
    document.getElementById('frame').ondragstart = function() { return false; };
  }

  handleBBoxUpdate(l,t,w,h){
    //console.log('passed values ' + l +' '+ t + ' ' + w + ' ' + h) 
    this.setState({
      l: l,
      t: t,
      h: h,
      w: w,
    });

  }

}


class BoundingBox extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      ElementStyle: {
        left: props.l + "px",
        top: props.t + "px",
        width: props.w + "px",
        height: props.h + "px",
        cursor: "move",
      },
      l: props.l,
      t: props.t,
      h: props.h,
      w: props.w,
      diffx: 0,
      diffy: 0,
      dragModeOn: false,
      resizeModeOn: false,
      possibleResizeModeOn: false, // if active -> do not drag
    };

    this.handleResize = this.handleResize.bind(this)
  }

  componentDidMount() {
        //in handle: remove 'mousedown' while mouse down, add when mouse up
        ReactDOM.findDOMNode(this).addEventListener('mousedown', this.handleMouseDown); 

        //in handle: remove 'mouseup' while mouse up, add when mouse down
        //in handle: remove 'mousemove' while mouse up, add when mouse down
  }

  render() {
    return (
      <div className="bbox" style={this.state.ElementStyle} >
        <ResizeBox posx={(this.state.w - 2.5) + "px"} posy={(this.state.h - 2.5) + "px"} 
          handleResize = {this.handleResize}
        ></ResizeBox>
      </div>
    );
  }

  handleResize = (event) => {
    if (event.type === 'mousedown'){
      //console.log('clicked: resize me')

      // enter resize state
      this.setState({
        resizeModeOn: true,
      });
      this.setState({          
        ElementStyle: {
          left: this.state.ElementStyle.left,
          top: this.state.ElementStyle.top,
          height: this.state.ElementStyle.height,
          width: this.state.ElementStyle.width,
          cursor: "nw-resize",
        }
      });
      document.addEventListener('mousemove', this.handleResize); 

    } else if (event.type === 'mousemove'){
      //console.log('listen for mouse-up')
      document.addEventListener('mouseup', this.handleMouseUp); 
      this.setState({          
        ElementStyle: {
          left: this.state.ElementStyle.left,
          top: this.state.ElementStyle.top,
          height: this.state.ElementStyle.height,
          width: this.state.ElementStyle.width,
          cursor: "nw-resize",
        }
      });

      this.setState({ 
        possibleResizeModeOn: true, 
      });
      if (this.state.resizeModeOn){
        var minwidthheight = 5;
        var offset_framearea = 100;

        // stop if width/height == 0
        if (this.state.w >= minwidthheight){
          // add mouse-position to width
          this.setState({
            w: Math.max(minwidthheight,event.pageX - this.state.l - offset_framearea),
          });

          this.setState({          
            ElementStyle: {
              left: this.state.ElementStyle.left,
              top: this.state.ElementStyle.top,
              height: this.state.ElementStyle.height,
              width: (this.state.w) + "px",
              cursor: "nw-resize",
            }
          });
        }
        if (this.state.h >= minwidthheight){
            // add mouse-position to height
            this.setState({
              h: Math.max(minwidthheight, event.pageY - this.state.t - offset_framearea),
            });

            this.setState({          
              ElementStyle: {
                left: this.state.ElementStyle.left,
                top: this.state.ElementStyle.top,
                height: (this.state.h) + "px",
                width: this.state.ElementStyle.width,
                cursor: "nw-resize",
              }
            });
          }


          // only pass changed values to parent
          this.props.handleBBoxUpdate(this.state.l,this.state.t,this.state.w,this.state.h); 
      }
    }
  }

  handleMouseDown = (event) => {
      //check if resize is on
      // if click is inside resize-box -> resize
      // otherwise -> drag

      if (this.state.possibleResizeModeOn === false){
        this.setState({
          dragModeOn: true,
          diffx: event.pageX - this.state.l,
          diffy: event.pageY - this.state.t,
        });
        document.addEventListener('mousemove', this.handleDrag);
        ReactDOM.findDOMNode(this).removeEventListener('mousedown', this.handleMouseDown);
        document.addEventListener('mouseup', this.handleMouseUp); 
      }
   }

   handleMouseUp = (event) => {
    //console.log('handle mouse up - remove mouse move listener')
    document.removeEventListener('mousemove', this.handleResize); 
      this.setState({          
        ElementStyle: {
          left: this.state.ElementStyle.left,
          top: this.state.ElementStyle.top,
          height: this.state.ElementStyle.height,
          width: this.state.ElementStyle.width,
          cursor: "move",
        }
      });

      this.setState({
        dragModeOn: false,
        possibleResizeModeOn: false, 
        resizeModeOn: false,
      }); 
      document.removeEventListener('mousemove', this.handleDrag); 
      ReactDOM.findDOMNode(this).addEventListener('mousedown', this.handleMouseDown);
      document.removeEventListener('mouseup', this.handleMouseUp);
   }

   handleDrag = (event) => {
      this.setState({          
        ElementStyle: {
          left: this.state.ElementStyle.left,
          top: this.state.ElementStyle.top,
          height: this.state.ElementStyle.height,
          width: this.state.ElementStyle.width,
          cursor: "move",
        }
      });
      if (this.state.dragModeOn === true) {
        this.setState({
          l: event.pageX - this.state.diffx,
          t: event.pageY - this.state.diffy,
        });

        this.setState({
          ElementStyle: {
            left: (event.pageX - this.state.diffx) + "px",
            top: (event.pageY - this.state.diffy) + "px",
            height: this.state.ElementStyle.height,
            width: this.state.ElementStyle.width,
            cursor: "move",
          }
        });
      }
      this.props.handleBBoxUpdate(this.state.l,this.state.t,this.state.w,this.state.h); 
    }
}



// little box on bbox's lower right corner
// resizes box when dragged
// child of Bounding Box
class ResizeBox extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      resizeModeOn: false,
    };
  }

  render() {
    return (
      <div className="resizebox" style = {{left: this.props.posx, top: this.props.posy}}
        onMouseDown ={this.props.handleResize} // called in parent BoundingBox
        onMouseMove ={this.props.handleResize}
      > 
      </div>
    );
  }

}

export default App
