      diffy: 0,
      dragModeOn: false,
    };
  }

  componentDidMount() {
        //in handle: remove 'mousedown' while mouse down, add when mouse up
        ReactDOM.findDOMNode(this).addEventListener('mousedown', this.handleMouseDown); 

