      this.setState({
        dragModeOn: false,
      }); 
      document.removeEventListener('mousemove', this.handleDrag); 
      ReactDOM.findDOMNode(this).addEventListener('mousedown', this.handleMouseDown);
      document.removeEventListener('mouseup', this.handleMouseUp);
   }

   handleDrag = (event) => {
