}


class BoundingBox extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      ElementStyle: {
        top: "10px",
        left: "10px",
        dragModeOn: false,
      }
    };
  }

  render() {
    return (
      <div className="bbox" style={this.state.ElementStyle} 
      onMouseDown={ this.handleEvent } 
      onMouseUp={ this.handleEvent } 
      onMouseMove = { this.handleEvent } 
      //onClick={() => this.handleDrag()}
      >
      </div>
    );
  }

  handleEvent = (event) => {
    console.log(event.type)

    if (event.type === "mousedown") {
      this.setState({
        ElementStyle: {
          top: this.state.ElementStyle.top,
          left: this.state.ElementStyle.left,
          dragModeOn: true,
        }
      });
    } else if (event.type === "mouseup") {
      this.setState({
        ElementStyle: {
          top: this.state.ElementStyle.top,
          left: this.state.ElementStyle.left,
          dragModeOn: false,
        }
      });
    } else if (event.type === "mousemove"){

      if (this.state.ElementStyle.dragModeOn)

      this.setState({
        ElementStyle: {
          top: this.state.ElementStyle.top,
          left: this.state.ElementStyle.left,
          dragModeOn: this.state.ElementStyle.dragModeOn,
        }
      });
    }
   }

   _onMouseMove(e) {
    console.log('move')
    this.setState({ x: e.screenX, y: e.screenY });
  }

  handleDrag(){
    this.setState({
      ElementStyle: {
        top: this.state.ElementStyle.top,
        left: "20px",
        dragModeOn: this.state.ElementStyle.dragModeOn,
      }
    });
    // reset x and y  (state/props)
  }

}

export default App
