import React, { Component } from 'react'
import ReactDOM from 'react-dom';



// color scheme https://www.schemecolor.com/coral-blue.php
class App extends Component {
  constructor(props) {
    super(props);
  }

  render() {
    return (
      <div className="App">
        <FrameArea>
        </FrameArea>
      </div>
    )
  }
}

// area shows the video and is the region where the bounding-box(es)
// can be moved 
class FrameArea extends React.Component {

  constructor(props) {
    super(props);

    // 100 = number of images
    // add empty bbox [0,0,0,0] for every frame
    var _trackerOutput = []
    var _num_imgs = 100;
    for (var i = 0; i < _num_imgs; i++) {
      _trackerOutput.push([0,0,0,0]);
    }
    _trackerOutput[0][0] = 464;
    _trackerOutput[0][1] = 113;
    _trackerOutput[0][2] = 31;
    _trackerOutput[0][3] = 32;

    this.state = { 
      trackerOutput: _trackerOutput, 
      frameNumber: 1, //start counting at 1
      maxFrameNumber: 1, // max frame number that has been labelled by tracker or manually
      imagesrc: "",
      // props for BoundingBox
      l: 464,
      t: 113,
      h: 31,
      w: 32,
      num_imgs: _num_imgs,
      SliderStyle: {
        width: "0%",
      },
    };

    this.handleBBoxUpdate = this.handleBBoxUpdate.bind(this)
  }

  // https://stackoverflow.com/questions/23450534/how-to-call-a-python-function-from-node-js
  // https://www.freecodecamp.org/news/create-a-react-frontend-a-node-express-backend-and-connect-them-together-c5798926047c/
  // ref: https://stackoverflow.com/questions/51845170/how-to-deploy-a-node-js-app-that-runs-a-python-script-in-azure
  //connect to node.js (code in api folder)
  runTracker() {
    console.log('run trackers')

    let f = this.state.frameNumber - 1
    let pythonCall = "http://localhost:9000/callPython/left/"
                      + this.state.trackerOutput[f][0] +"/top/"
                      + this.state.trackerOutput[f][1] +"/width/"
                      + this.state.trackerOutput[f][2] +"/height/"
                      + this.state.trackerOutput[f][3] +"/frameno/" 
                      + f + "/"


    console.log(pythonCall);
    fetch(pythonCall) 
      .then(res => res.text())
      //.then(res => this.setState({ trackerOutput: (res)} )); 
      .then(res => this.handleTrackerOutput(res) );      
  }

  render(){

    const boundingBoxes = [];

    console.log('render')
    console.log(this.state.trackerOutput.length);
    //console.log(this.state.trackerOutput);
    console.log(this.state.frameNumber);


    for (var i = this.state.frameNumber-1; i < this.state.trackerOutput.length; i += 1) { // this.state.numChildren

      //console.log('add? ' + this.state.trackerOutput[i][0] + ' ' + this.state.trackerOutput[i][1]+ ' ' + this.state.trackerOutput[i][2]+ ' ' + this.state.trackerOutput[i][3]) 
      if(this.state.trackerOutput[i][0] !== undefined 
        && this.isValidBBox(this.state.trackerOutput[i])){
        let _classname = "bbox"
        let _isSelected = false
        if (this.state.frameNumber-1 === i){
          _classname = "bbox selected"
          _isSelected = true
          console.log('selected bbox')
        }

        console.log('add ' + this.state.trackerOutput[i])

        boundingBoxes.push(<BoundingBox 
          l = {this.state.trackerOutput[i][0]} 
          t = {this.state.trackerOutput[i][1]} 
          w = {this.state.trackerOutput[i][2]} 
          h = {this.state.trackerOutput[i][3]}  
          key={i} 
          number={i}
          classname = {_classname}
          isSelected = {_isSelected}
          frameNumber = {this.state.frameNumber}
          handleBBoxUpdate = {this.handleBBoxUpdate} />);
      }
    };

    const boundingBoxes_values = [];
    for (i = 0; i < this.state.trackerOutput.length; i ++){
      boundingBoxes_values.push(
        <p key={i}>{this.state.trackerOutput[i][0] + ' ' + this.state.trackerOutput[i][1] + ' ' + this.state.trackerOutput[i][2] + ' ' + this.state.trackerOutput[i][3] }</p>
      );
    }

    return (
      <div id="FrameArea">
      {boundingBoxes}
      <img id = "frame" src={this.state.imagesrc}  alt="" width="500" draggable="false" ></img>
      <button id="btn_runtracker" onClick = {() => this.runTracker()}>run tracker</button>
      <div className="slidecontainer" style = {this.state.SliderStyle}>
        <input type="range" min="1" max={this.state.maxFrameNumber} value={this.state.frameNumber} className="slider" id="myRange" onChange={this.handleSliderChange} onInput = {this.handleSliderInput} />
        <p>{this.state.frameNumber}</p>
      </div>
      <div>{boundingBoxes_values}</div>
      </div>
    )
  }

  handleTrackerOutput(res){
    console.log(res)
