import React from 'react';
import ReactDOM from 'react-dom';

class Slider extends React.Component {

    //<Slider max_value={this.state.maxFrameNumber} max_tracked = {this.state.max_tracked}  min_value = {1} value={this.state.frameNumber} />

    constructor(props) {
      super(props);
      this.state = {
        startPosX: undefined, 
        offsetX: undefined,
        dragModeOn: false,
        selectedStyle: {
          backgroundColor: "blue",
          width: "0%",
          height: "20px",
        },
        bgStyle: {
          backgroundColor: "#dedede", // light gray
          width: "100%",
          height: "20px",
        },
        trackedStyle: {
          backgroundColor: "light-blue",
          width: "0%",
          height: "20px",
        },
        thumbStyle: {
            left: "0px",
        },
      };
    }

      
    componentDidMount() {
  
        ReactDOM.findDOMNode(this).addEventListener('mousedown', this.handleSliderInput); 
        ReactDOM.findDOMNode(this).addEventListener('mousemove', this.handleSliderInput); 
    }

    handleSliderInput = (event) => {

        if (event.type === "mousedown"){
            console.log('slider mousedown')
          this.setState({
            startPosX: event.pageX,
            dragModeOn: true,
          });
        } else if (event.type === "mouseup"){
            console.log('slider mouseup')

            //update frame no depending on offset

            var sliderWidth =  ReactDOM.findDOMNode(this).getElementsByClassName('slidecontainer')[0].offsetWidth;// - 25;
            var one_slider_unit = (sliderWidth/100);

            //convert pixel offset to slider value
            //this.props.value
            //this.props.setFrameNo(frameno)

        } else if (event.type === "mousemove"){
            console.log('slider mousemove')
            //on move: register offset from start pos
            // set thumb position

            if (this.state.dragModeOn === true){

                var sliderX =  ReactDOM.findDOMNode(this).getElementById('slidecontainer')[0].offsetX;
                var xPos = event.pageX;
                this.setState({
                    offsetX: this.state.startPosX - this.state.xPos,
                    thumbStyle: {
                        left: xPos - sliderX,
                    },
                  });
            }

    
          // 1. change framenumber
          // 2. update image
          //console.log('event.target.value = ' + clickPosX)
          //console.log('sliderWidth = ' + sliderWidth)
          //console.log("(sliderWidth/100) = " + (sliderWidth/100))
          //console.log("Math.max(event.target.value-3,2) = " +  Math.max(clickPosX-3,2))
          //console.log("result = " + ((sliderWidth/100) * Math.max(clickPosX-3,2)) + "px")
        }
      }
  
    render() {
      return(
        <div style = {this.state.bgStyle} className="slidecontainer">
          <div id="Thumb" style = {this.state.thumbStyle} onMouseDown = {this.props.handleSliderInput} onMouseUp = {this.props.handleSliderInput}>
            <p>{this.state.frameNumber}</p>
          </div>
        </div>
      );
    }
  }

  export default Slider;
