          // onDragStart={this.handleDragStart}
          onDrag={this.handleDrag}
          // onDragEnd={this.handleDragEnd}
        >
        </ResizableRect> */}

        <BoundingBox></BoundingBox>
      </div>
    )
  }
}

class Area extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      mouseUp: true,
    };
  }

  render() {
    return (
      <div className="frame_area"
      onMouseDown={ this.handleEvent } 
      onMouseUp={ this.handleEvent } 
      >
      </div>
    );
  }

  handleEvent = (event) => {
    if (event.type === "mousedown") {
      this.setState({
        mouseUp: false,
      });
    } else if (event.type === "mouseup") {
      this.setState({
        mouseUp: true,
      });
    } 
   }

}


class BoundingBox extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      ElementStyle: {
        top: "10px",
        left: "10px",
        width: "50px",
        height: "50px",
      },
      x: 10,
      y: 10,
      h: 50,
      w: 50,
      diffx: 0,
      diffy: 0,
      dragModeOn: false,
    };
  }

  componentDidMount() {
        // remove 'mousedown' while mouse down, add when mouse up
        ReactDOM.findDOMNode(this).addEventListener('mousedown', this.handleMouseDown); 

        //remove 'mouseup' while mouse up, add when mouse down
        //remove 'mousemove' while mouse up, add when mouse down
  }


  render() {
    return (
      <div className="bbox" style={this.state.ElementStyle} >
      </div>
    );
  }

  handleMouseDown = (event) => {
      this.setState({
        dragModeOn: true,
        diffx: event.pageX - this.state.x,
        diffy: event.pageY - this.state.y,
      });
      document.addEventListener('mousemove', this.handleDrag);
      ReactDOM.findDOMNode(this).removeEventListener('mousedown', this.handleMouseDown);
      document.addEventListener('mouseup', this.handleMouseUp); 
   }

   handleMouseUp = (event) => {
      this.setState({
        dragModeOn: false,
      }); 
      document.removeEventListener('mousemove', this.handleDrag); 
      ReactDOM.findDOMNode(this).addEventListener('mousedown', this.handleMouseDown);
      document.removeEventListener('mouseup', this.handleMouseUp);
   }

   handleDrag = (event) => {

      if (this.state.dragModeOn == true) {
        this.setState({
          x: event.pageX - this.state.diffx,
          y: event.pageY - this.state.diffy,
        });

        this.setState({
          ElementStyle: {
            top: (event.pageY - this.state.diffy) + "px",
            left: (event.pageX - this.state.diffx) + "px",
            height: this.state.ElementStyle.height,
            width: this.state.ElementStyle.width,
          }
        });
      }
    }



}

