import React from 'react';
import ReactDOM from 'react-dom';

class Slider extends React.Component {


    // enter -> move to next frame number but do not overwrite bbox if they exist (not [0000])
    // tracker -> extend maxtracked, overwrite following bboxes

    //'glue' to pointer
    // x click on slider to select frame
    // new example vid (pedestrian from http://www.votchallenge.net/vot2018/dataset.html)
    // same width img and slider

    //todo: bug box rendered at mousepos
    //todo some issue at last frame / run tracker
    // bug moving bbox swaps frame?
    // bug slider width/thumb pos
    // scale image / bbox by some factor
    // send baseimgpath to python also

    //finally: upload to azure (install cv2)

    //<Slider max_value={this.state.num_imgs} max_tracked = {this.state.maxFrameNumber}  min_value = {1} value={this.state.frameNumber} setFrameNo = {this.setFrameNo} colorPalette = {this.state.colorPalette}/>

    constructor(props) {
      super(props);
      console.log(this.props)
      this.state = {
        startPosX: 0, 
        offsetX: 0,
        dragModeOn: false,
        sliderWidth: this.props.imagewidth,
        sliderwidthset: false,
        bgStyle: {
            width: this.props.imagewidth,
            backgroundColor: this.props.colorPalette.LightGray,
            height: "20px",
            borderRadius: "0px",
            display: "inline-block",
        },
      };
    }

    componentDidMount() {
        ReactDOM.findDOMNode(this).addEventListener('mousedown', this.handleSliderInput); 
        document.addEventListener('mousemove', this.handleSliderInput); 
        document.addEventListener('mouseup', this.handleSliderInput); 

    }

    componentDidUpdate() {
        if(this.state.sliderwidthset === false){
            this.getSliderWidth();
        }
    }

    getSliderWidth(){
        /*
        this.setState({
            sliderWidth: ReactDOM.findDOMNode(this).getBoundingClientRect().width, 
            sliderwidthset: true,
        });*/
    }

    handleSliderInput = (event) => {
        //TODO do not call all the time ... (handle resize)
        this.getSliderWidth();

        if (event.type === "mousedown"){
            console.log('slider mousedown')
          this.setState({
            startPosX: event.pageX,
            dragModeOn: true,
          });
          //document.addEventListener('mousemove', this.handleSliderInput); 

        } else if (event.type === "mouseup"){ // todo as soon as trackerbtn unclicked
            console.log('slider mouseup')

            //update frameno depending on offset
            //convert pixel offset to slider value
            //console.log(this.state.selectedFrameNumber)
            //var sliderWidth =  ReactDOM.findDOMNode(this).offsetWidth;
            this.props.setFrameNo(this.props.value) //run tracker changes this to maxtracked
            this.setState({
                offsetX: event.pageX - this.state.startPosX,
                dragModeOn: false,
              });
              console.log(this.state.offsetX)
              //document.removeEventListener('mousemove', this.handleSliderInput); 

        } else if (event.type === "mousemove"){
            // on move: register offset from start pos
            // allow slide only to already tracked/manually marked frames

            //convert framenumber to position on slider .. 
            // slider is either set manually or through pressing enter/tracking
            // set to frame number

            if (this.state.dragModeOn === true){

                //var sliderWidth =  ReactDOM.findDOMNode(this).offsetWidth - 25;
                console.log('sliderWidth ' + this.props.imagewidth)

                var xPos = event.pageX;
                var new_left = xPos - 100; // 100 is left of FrameArea
                new_left = Math.max(this.props.min_value, new_left)

                var newFrameNumber = Math.ceil((new_left/this.props.imagewidth) * this.props.max_value);
                newFrameNumber = Math.min(newFrameNumber, this.props.max_tracked)
                console.log(this.props.max_tracked)
                console.log('newFrameNumber ' + newFrameNumber)
                //new_left = Math.min((this.props.max_tracked-1) * (sliderWidth/100), new_left)
                this.setState({
                    offsetX:  xPos - this.state.startPosX,
                    selectedFrameNumber: newFrameNumber,
                }); 
                console.log('startPosX ' + this.state.startPosX)
                console.log('offsetx ' + this.state.offsetX)
                this.props.setFrameNo(newFrameNumber)
            }
        }
      }
  
    render() {
        console.log('left of thumb selectedFrameNumber '+ (this.state.selectedFrameNumber))
        console.log('left of thumb imagewidth '+ this.props.imagewidth)

      return(
        <div style = {this.state.bgStyle} className="backgroundSlider">
        <div style = {{
            width: (this.props.max_tracked) + '%', 
            backgroundColor:"#A9A9A9", 
            height:"20px", 
            top: "0px", 
            position: "relative"}} 
            className="trackedSlider"/>
            <div style = {{
            backgroundColor: this.props.colorPalette.RoyalBlue,
            width: (this.props.value-1) +"%",
            height: "20px",
            borderRadius: "0px",
            position: "relative",
            top: "-20px",}} 
            className="selectedSlider"/>


        <div id="Thumb" className="Thumb" style = {{
            left: (this.props.selectedFrameNumber-1) * (this.props.imagewidth/100), // this.props.imagewidth
            //left: this.state.left + this.state.offsetX,
            backgroundColor: this.props.colorPalette.LightSalmon,
        }
        } onMouseDown = {this.props.handleSliderInput} onMouseUp = {this.props.handleSliderInput}>
            <p style = {{
                align:"center", 
                textAlign: "center", 
                verticalAlign: "middle",
                top: "25px", 
                position: "relative",
                }}>{this.props.value}</p>
        </div>
        </div>
      );
    }
  }

  export default Slider;
