import React from 'react';
import ReactDOM from 'react-dom';

class Slider extends React.Component {

    //todo
    // max selectable val: max tracked (general: skip over frames /wo move)
    // color selected bar, tracked/marked bar
    // move only if clicked
    // enter -> move slider
    //instant update
    //impl left right arrows to slide

    //<Slider max_value={this.state.maxFrameNumber} max_tracked = {this.state.max_tracked}  min_value = {1} value={this.state.frameNumber} />

    constructor(props) {
      super(props);
      this.state = {
        startPosX: undefined, 
        offsetX: undefined,
        dragModeOn: false,
        selectedFrameNumber: this.props.value, //Todo remove?
        selectedStyle: {
          backgroundColor: "blue",
          width: "0%",
          height: "20px",
          borderRadius: "10px",
        },
        bgStyle: {
          backgroundColor: "#dedede", // light gray
          height: "20px",
          borderRadius: "10px",
        },
        trackedStyle: {
          backgroundColor: "light-blue",
          width: "0%",
          height: "20px",
          borderRadius: "10px",
        },
        thumbStyle: {
            left: "0px",
        },
      };
    }

      
    componentDidMount() {
        ReactDOM.findDOMNode(this).addEventListener('mousedown', this.handleSliderInput); 
        document.addEventListener('mousemove', this.handleSliderInput); 
        document.addEventListener('mouseup', this.handleSliderInput); 
    }

    handleSliderInput = (event) => {

        if (event.type === "mousedown"){
            console.log('slider mousedown')
          this.setState({
            startPosX: event.pageX,
            dragModeOn: true,
          });
        } else if (event.type === "mouseup"){
            console.log('slider mouseup')

            //update frameno depending on offset
            //convert pixel offset to slider value
            console.log(this.state.selectedFrameNumber)
            this.props.setFrameNo(this.state.selectedFrameNumber)
            this.setState({
                offsetX: this.state.startPosX - this.state.xPos,
                dragModeOn: false,
              });

        } else if (event.type === "mousemove"){
            //on move: register offset from start pos
            // set thumb position

            if (this.state.dragModeOn === true){
                //var sliderX = ReactDOM.findDOMNode(this).offsetLeft;//.getBoundingClientRect();
                //console.log(rect.top, rect.right, rect.bottom, rect.left);
                var sliderWidth =  ReactDOM.findDOMNode(this).offsetWidth - 25;
                var xPos = event.pageX;
                var new_left = xPos - 100;
                new_left = Math.max(this.props.min_value, new_left)
                new_left = Math.min(sliderWidth, new_left)

                this.setState({
                    offsetX: this.state.startPosX - this.state.xPos,
                    thumbStyle: {
                        left: new_left, // 100 is left of FrameArea
                    },
                  });

                
                 this.setState({
                    selectedFrameNumber: Math.ceil((this.state.thumbStyle.left/sliderWidth) * this.props.max_value),
                    selectedStyle: {
                        backgroundColor: "light-blue",
                        width: ((this.state.thumbStyle.left/sliderWidth) * this.props.max_value) +"%",
                        height: "20px",
                        borderRadius: "10px",
                      },
                 }); 



                 this.props.setFrameNo(Math.ceil((this.state.thumbStyle.left/sliderWidth) * this.props.max_value))

            }
        }
      }
  
    render() {
      return(
        <div style = {this.state.bgStyle} className="backgroundSlider">
        <div style = {this.state.selectedStyle} className="selectedSlider"/>
        <div style = {this.state.trackedStyle} className="trackedSlider"/>

        <div id="Thumb" className="Thumb" style = {this.state.thumbStyle} onMouseDown = {this.props.handleSliderInput} onMouseUp = {this.props.handleSliderInput}>
            <p>{this.state.selectedFrameNumber}</p>
            <p>{'val ' + this.props.value}</p>
        </div>
        </div>
      );
    }
  }

  export default Slider;
